func:
// Logger
const logger = global.get("logger");

// INFLOW
let strategy = flow.get("house_battery_strategy");

// Configure msg.strategy object
RED.util.setMessageProperty(msg,"strategy.selected",strategy,true);
RED.util.setMessageProperty(msg,"strategy.trace",[],true);

// stop on error
if(msg.batteries === undefined) {
    logger(this, "Battery configuration undefined", "error");
    return null;
}
if(strategy === undefined) {
    logger(this, "Battery strategy undefined", "error");
    return null;
}

// select target flow based on the input_select `house_battery_strategy`
msg.target = `${strategy}`;

// full stop trigger overrules ALL selected strategies
const stopTrigger = RED.util.getMessageProperty(msg,"full_stop_trigger");
if(stopTrigger == "on" || stopTrigger === true || stopTrigger === 1) {
    msg.target = 'Full stop';
    // Explain
    logger(this, `**Full Stop** strategy selected, due to (EV) stop trigger: '${stopTrigger}'`);
}

// add execution marker
RED.util.setMessageProperty(msg,"strategy.execution_start",Date.now()); // when do we start executing the selected strategy

// OUTPUT
node.status({fill:"grey",shape:"dot",text:msg.target});
return msg;

// add execution marker
let execution_end = Date.now();
RED.util.setMessageProperty(msg, "strategy.execution_end", execution_end);

// retrieve execution markers
let execution_start = RED.util.getMessageProperty(msg,"strategy.execution_start");

// report execution time
if (execution_start !== undefined && execution_end !== undefined) {
    // exec time
    let execution_time = (execution_end - execution_start) / 1000; // seconds
    // report
    if (execution_time <= 0) node.status({ fill: "red", shape: "dot", text: `negative or zero timing` });
    if (execution_time > 0) node.status({ fill: "green", shape: "dot", text: `${execution_time} sec`});
    if (execution_time >= 0.7) node.status({ fill: "yellow", shape: "dot", text: `${execution_time} sec` });
    if (execution_time >= 1) node.status({ fill: "red", shape: "dot", text: `${execution_time} sec` });

} else {
    node.status({fill:"grey",shape:"dot",text:"timing error"});
}

return msg;

// loop through the number of batteries set by the user
msg.battery_index = 1; // base-1

// Init an array for battery status and value information
msg.batteries = [];

// Continue
return msg;

// Map to batteries array
msg.batteries.push({
    id: `M${msg.battery_index}`,
    power: parseInt(msg.battery_power,10),
    charging_max: parseInt(msg.max_charge_power,10),
    discharging_max: parseInt(msg.max_discharge_power,10),
    soc: parseInt(msg.battery_state_of_charge,10),
    soc_max: parseInt(msg.charging_cutoff_capacity,10),
    soc_min: parseInt(msg.discharging_cutoff_capacity,10),
    inverter: String(msg.inverter_state),
    energy: Number(msg.battery_remaining_capacity),
    energy_max: Number(msg.battery_total_energy),
    rs485: String(msg.rs485_control_mode)
    });

// Continue
return msg;

// Logger
const log = global.get("logger");

// enums
const CMODE = {
    STOP: "stop", // Marstek batteries disconnect a relay when this is set or Power is 0W
    CHARGE: "charge",
    DISCHARGE: "discharge"
}

// Output format and target
let outputArray = [];
let solution = msg.solutions[msg.battery_index-1]; // Base-0, thus -1
if (solution === undefined) {
    log(this,`Can't find solution for index ${msg.battery_index - 1} in ${msg.solutions}, aborting...`,"error");
    return null;
}
let target = `marstek_${solution.id.toLowerCase()}`;

// Safety | Id exists
let battery = msg.batteries.find(battery => battery.id === solution.id);
if (battery === undefined) {
    log(`Can't set battery ${solution.id}, aborting...`,"error");
    return null;
}
// Safety | Power limit
solution.power = Math.min(solution.power, solution.mode == CMODE.CHARGE ? battery.charging_max: battery.discharging_max);

// Set | Mode
const serviceCallMode = {
    "action": "select.select_option",
    "target": {
        "entity_id": [`select.${target}_forcible_charge_discharge`]
    },
    "data": {
         "option": String(solution.mode) // forced charge mode (stop, charge, discharge)
    }
};
// Add topic to allow correct 'on change' filtering
outputArray.push({payload: serviceCallMode, topic:solution.id});

// Set | Power
const serviceCallPower = {
    "action": "number.set_value",
    "target": {
        "entity_id": [
            `number.${target}_forcible_charge_power`,
            `number.${target}_forcible_discharge_power`
            ]
    },
    "data": {
        "value": Number(solution.power)
    } 
};
// Add topic to allow correct 'on change' filtering
outputArray.push({payload: serviceCallPower, topic:solution.id});

// Store service calls in msg to allow checking of loop results
msg.loop_result = { target, serviceCallMode, serviceCallPower };

// Return msg as third output
outputArray.push(msg);

// Explain
log(this,`${target} ${String(solution.mode)} @ ${Number(solution.power)} Watt`);

// Output
return outputArray;

// loop through the number of batteries set by the user
msg.battery_index = msg.battery_count;

// Debug
RED.util.setMessageProperty(msg, "debug", `## Cohort ${Date.now()} ##`);

// Continue
return msg;

// Step index down
msg.battery_index -= 1;

// Continue
return msg;

RED.util.setMessageProperty(msg,"setbatteries.execution_start",Date.now());
RED.util.setMessageProperty(msg,"setbatteries.marker", `M1_${Date.now()}`);
return msg;

let start = RED.util.getMessageProperty(msg,"setbatteries.execution_start");
let end = Date.now();
let delta = end - start;
let timingString = `${delta/1000} sec`;

node.status({fill:"green",shape:"dot",text: timingString});
msg.delta = delta;
msg.timing = timingString;

return msg;

// Step index down
msg.battery_index += 1;

// Continue
return msg;

// Cycles battery priority as follows
// M=1 [1,2,3,4] | M=2 [2,3,4,1] | M=3 [3,4,1,2] | etc.
// With M the battery to prioritize

// msg.batteries (original order)
let currentArray = msg.batteries;

// Prioritize the Mth battery
const M = RED.util.getMessageProperty(msg,"prioritize_battery") || 1;

// 1st battery has prio? Skip and continue without change
if (M==1) return msg;

// N equals the number of items to take from the front to the back of the array, effectivly rotating battery priority
const N = M - 1; // base-0 version of M
node.status({fill:"blue",shape:"ring",text:`N = ${N}`});

// 1. Check if the array is valid and long enough
if (!Array.isArray(currentArray) || currentArray.length < N) {
    // Send an error or the original array back if the array is invalid/too short
    node.error(`Array is not valid or shorter than ${N} items.`, msg);
    return null;
}

// 2. Get the first N items (these will go to the back)
// slice(0, N) retrieves elements from the start (index 0) up to index N (exclusive).
let firstN = currentArray.slice(0, N);

// 3. Get the remaining items (these will stay at the front)
// slice(N) retrieves elements from index N to the end of the array.
let remaining = currentArray.slice(N);

// 4. Concatenate them: remaining items (front) + first N items (back)
let newArray = remaining.concat(firstN);

// 5. Place the new array back into msg.batteries
msg.batteries = newArray;

return msg;

// Current prioritized battery is the Mth battery
let M = RED.util.getMessageProperty(msg,"prioritize_battery") || 1;

// Prioritize the next battery
M += 1;

// If the next battery does not exist, prioritize the first again
if(M > msg.battery_count) M = 1;

// Pass along as payload
msg.payload = M;

// Show which battery has priority
node.status({fill:"green",shape:"dot",text:`Prioritize battery #${M}`});

// Done
return msg;

// Manual priority, never pass
if(msg.payload == "Never") {
    node.status({fill:"yellow",shape:"dot",text:"Auto cycle disabled"});
    return null;
}

// Daily interval, always pass
if(msg.payload == "Daily") {
    node.status({ fill: "green", shape: "dot", text: `Pass` });
    return msg;
}

// Weekly interval
const today = new Date();
// Get the day of the week (0 = Sunday, 1 = Monday, ..., 6 = Saturday)
const dayOfWeek = today.getDay();

// Check if the day is Sunday (which is represented by the number 0)
if (dayOfWeek === 0) {
    // If it is Sunday, pass
    node.status({ fill: "green", shape: "dot", text: `Passed on Sunday` });
    return msg;
} else {
    // If it is not Sunday, stop
    node.status({ fill: "yellow", shape: "dot", text: `Halted, not Sunday` });
    return null;
}

// batteries | calculate total and max. power delivered by batteries
let batteries = msg.batteries;
let batteries_total_power = 0;
let batteries_max_charging_power = 0;
let batteries_max_discharging_power = 0;
let batteries_available_energy = 0;
let batteries_max_energy = 0;

batteries.forEach(battery => {
    batteries_total_power += Number(battery.power);
    batteries_max_charging_power += Number(battery.charging_max);
    batteries_max_discharging_power += Number(battery.discharging_max);
    batteries_available_energy += Number(battery.energy - battery.energy_max*battery.soc_min/100);
    batteries_max_energy += (Number(battery.energy_max*battery.soc_max) - Number(battery.energy_max*battery.soc_min))/100; // kWh
});

// OUTPUT
RED.util.setMessageProperty(msg, "batteries_total_power", batteries_total_power, true); // W
RED.util.setMessageProperty(msg, "batteries_max_charge_power", batteries_max_charging_power,true); // W
RED.util.setMessageProperty(msg, "batteries_max_discharge_power", batteries_max_discharging_power,true); // W
RED.util.setMessageProperty(msg, "batteries_available_energy",batteries_available_energy,true); // kWh usable
RED.util.setMessageProperty(msg, "batteries_max_energy", batteries_max_energy, true); // kWh usable
return msg;

// cleanup 
delete msg.battery_index;

delete msg.battery_power;
delete msg.max_charge_power;
delete msg.max_discharge_power;
delete msg.battery_state_of_charge;
delete msg.charging_cutoff_capacity;
delete msg.discharging_cutoff_capacity;
delete msg.inverter_state;
delete msg.battery_remaining_capacity;
delete msg.battery_total_energy;
delete msg.rs485_control_mode;

return msg;

// Switch between low and high rate limiting to decrease load and power consumption
// --
// The flow should always be rate limited by atleast the total execution time of strategies
// The flow should not lock up indefinitly when grid_power is constant.
// In the latter case, a more relaxed rate limit can help reduce system load.
// Devs: don't set msg.rate directly. Use nodes, so novice users can easily read the flow.

// Thresholds for change in power, before switching to high rate limit
const THRESHOLD_POWER = 20; // Watt change
const THRESHOLD_PERCENT = 2; // Percentage
RED.util.setMessageProperty(msg, "p1_rate_limit.threshold_power", THRESHOLD_POWER,true);
RED.util.setMessageProperty(msg, "p1_rate_limit.threshold_percent", THRESHOLD_PERCENT, true);

// Get the previous value from this node's context
// If it doesn't exist yet, initialize it as null
let previousValue = context.get('previousValue') || null;
let currentValue = Number(msg.grid_power);

// Save the current value for the next time the node is triggered
context.set('previousValue', currentValue);

// Default rate limiting
RED.util.setMessageProperty(msg,"p1_rate_limit.save_power",true);

// Check if we have a valid history to compare with
if (previousValue !== null) {
    let powerChange = Math.abs(currentValue - previousValue);
    let percentageChange = previousValue !== 0 ? Math.abs((powerChange / previousValue) * 100):0;

    // Condition: Absolute difference > N Watt AND percentage change > M %
    if (powerChange > THRESHOLD_POWER && percentageChange > THRESHOLD_PERCENT) {
        // Enable faster refresh rate
        RED.util.setMessageProperty(msg,"p1_rate_limit.save_power",false);
    }
}

return msg;

// Logger
const logger = global.get("logger");

// Passed or blocked
const hasPassed = msg.payload; // 0 = blocked, 1 = passed

// 1. Get evaluations (Total attempts)
let evaluations = (context.get("p1_rate_limit_evaluations") || 0) + 1;
context.set("p1_rate_limit_evaluations", evaluations);

// 2. Increment and get passes (Filtered/successful events)
let passes = (context.get("p1_rate_limit_passes") || 0) + hasPassed;
context.set("p1_rate_limit_passes", passes);

// 3a. Calculate percentage (Rate of passing vs total evaluations)
let percentage = evaluations > 0 ? (passes / evaluations) * 100 : 0;
// 3b. Display the percentage rate of not-passing as a measure of efficiency gained
percentage = 100 - percentage;

// 4. Update Node Status
node.status({
    fill: "blue",
    shape: "dot",
    text: `Saved: ${percentage.toFixed(2)}% (${evaluations-passes}/${evaluations})`
});

// 5. Explain
// On block
if(hasPassed === 0) {
    if(msg.p1_rate_limit.save_power === true) {
        // This is good
        logger(this, `<font color="#009ac7">Power saved</font> by P1 Rate Limiter, execution stopped.`); 
    } else {
        // This is not so good. P1 updates are coming in fast.
        logger(this, `<font color="orange">Blocked</font> by P1 Rate Limiter. ${Number(1000/msg.rate).toFixed(2)} messages per second exceeded.`); 
    }

    // Send message onward to debug dashboard
    return msg;
}
// No output on pass
return null;

// Block when undefined (don't update UI)
if(msg.ui_value_current === undefined) return null;

// We show the 2nd strategy in the trace (if available) on the dashboard as the 'active sub-strategy'
const secondTraceEntry = msg.strategy?.trace?.[1];
if (secondTraceEntry) {
    // Handle case where 2nd flow is present
    msg.ui_value_new =  secondTraceEntry.flow;
} else {
    // Handle case where the 2nd flow is missing
    // Get a sensible alternative
    msg.ui_value_new = msg.strategy?.selected || ""; // don't use unknown or unavailable as these are reserved HA keywords
}

// Improve human readability
if(msg.ui_value_new == "Self-consumption" && secondTraceEntry?.flow_settings?.discharge_disabled === true){
    msg.ui_value_new = "Self-consumption (charge only)";
}
if(msg.ui_value_new == "Self-consumption" && secondTraceEntry?.flow_settings?.charge_disabled === true){
    msg.ui_value_new = "Self-consumption (no charging)";
}

// Node status for devs
node.status({fill:"grey",shape:"dot",text:`new: ${msg.ui_value_new}`});

// Block when equal (don't update UI)
if(msg.ui_value_current === msg.ui_value_new) return null;

// Pass otherwise
return msg;

/**
 * Custom Logger Function
 * * Provides centralized logging, node-status updates, and message tracing.
 * Specifically designed for Home Battery Control logic within Node-RED.
 * 
 * Debug dashboard updating:
 * A msg needs to reach 'Strategy Evaluation' or be passed to the "Show on debug board" trigger node.
 * Errors are automatically passed to the "Show on debug board" trigger node.
 * 
 * * @param {object} callingNode - The context of the calling node (pass 'this').
 * @param {string} text - The log message/explanation.
 * @param {string} level - Log level: 'log', 'warn', 'error', or 'info' (default: 'info').
 * 
 * Note: anything above 'info' will also write to the Node-RED log files 
 * 
 * * @usage: 
 * const log = global.get("logger");
 * log(this, "Charging cycle started");
 */
const customLogger = (callingNode, text, level = 'info') => {

    // Log only when debug_mode is ON
    const debug_mode = global.get("debug_mode") || false;
    if(debug_mode == false) return null;

    // Max log elements to keep
    const MAX_ELEMENTS = 100;

    // Create the YYYY-MM-DD HH:MM:SS.mmm format
    const now = new Date();
    const timestamp = now.getHours().toString().padStart(2, '0') + ":" +
                now.getMinutes().toString().padStart(2, '0') + ":" +
                now.getSeconds().toString().padStart(2, '0') + "." +
                now.getMilliseconds().toString().padStart(3, '0');

    const formattedTimeLevel = `${timestamp} [${level.toLowerCase()}]`;

    // Get the name of the node or fallback to the ID
    const nodeName = callingNode.__node__ ? callingNode.__node__.name || callingNode.__node__.id : undefined;
    // Get the name of the Flow/Tab
    const flowName = callingNode.env.get("NR_FLOW_NAME") || "Unknown flow";
    // Explain to the Home Battery Control user what is going on
    const formattedExplenation = `[${flowName} >> ${nodeName}]: ${text}`;

    // Level icons
    let icon = "?";
    switch (level) {
        case "info":
            icon = "??";
            break;
        case "log":
            icon = "??";
            break;
        case "warn":
            icon = "??";      
            break;
        case "error":
            icon = "?";
            break;
    }

    // Enrich msg with log info
    const msg = callingNode.msg;
    (msg.log = msg.log || []).push({ timestamp: timestamp, level:level, flow:flowName, node:nodeName, payload: text, icon: icon});

    if (msg.log.length > MAX_ELEMENTS) {
        // Slice keeps the last X elements
        msg.log = msg.log.slice(-MAX_ELEMENTS);
    }

    // Log to the Node-RED system log
    switch (level) {
        case "log":
            node.log(`${formattedExplenation}`);
            break;
        case "warn":
            node.warn(`${formattedExplenation}`);       
            break;
        case "error":
            node.error(`${formattedExplenation}`, callingNode.msg);
            break;
        default:
            // don't log to logfiles by default
    }

};

global.set('logger', customLogger);
global.set('unhandledException', "");
return msg;

// enable or disable Insights Mode
if(msg.debug_mode == "on"){
    // ENABLED - explain to user
    const log = global.get("logger");
    log(this, "P1 Rate limiter is *disabled* during *debug mode*");

    // Set global (setting this via current state node has proven irriliable)
    global.set("debug_mode", true);
} else {
    // DISABLED - set global
    global.set("debug_mode", false);
}

// Clean up - only use the global
delete msg.debug_mode;

return msg;

/**
 * Handles exceptions by extracting node details and routing them to a global logger.
 * This is designed to be called from within a Node-RED Function node or sub-flow.
 *
 * @param {Object} callingNode - The Node-RED 'this' context or msg object containing node metadata.
 * @param {Object} [callingNode.msg] - The message object associated with the error.
 * @param {Object} [callingNode.msg.error] - Error details provided by a Node-RED error catch.
 * @param {string} [callingNode.msg.error.message] - The specific error message.
 * @param {Object} [callingNode.msg.error.source] - The node that triggered the error.
 * * @example
 * // Usage in a Function Node:
 * const unhandledException = global.get('unhandledException');
 * unhandledException(this);
 */
const unhandledException = (callingNode) => {
    // 1. Get the custom logger from global context
    const log = global.get("logger");

    // 2. Extract error details from the msg object
    const errorData = callingNode.msg?.error || {};
    const errorMessage = errorData.message || "Unknown error occurred";
    const sourceName = errorData.source ? (errorData.source.name || errorData.source.id) : "Unknown Node";
    const sourceType = errorData.source ? errorData.source.type : "unknown-type";

    // 3. Construct a user-friendly message for the logger
    const friendlyMessage = `**Unhandled exception** (${sourceType} node) ${errorMessage}`;
    callingNode.__node__ ??= {};
    callingNode.__node__.name = sourceName;

    // 4. Call the logger with level 'error'
    // Note: the logger uses 'callingNode', we pass on whatever we have.
    log(callingNode, friendlyMessage, 'error');

};

global.set('unhandledException', unhandledException);

return msg;

const handle = global.get('unhandledException');

handle(this);

return msg;

// array for output 
let outputArray = [];

// battery to target
const target = `marstek_m${msg.battery_index}`;

// Set | work mode
const workModeAction = {
    "action": "select.select_option",
    "target": {
        "entity_id": [`select.${target}_user_work_mode`]
    },
    "data": {
         "option": String(msg.work_mode)
    }
};
// Add to output array. Set null for no action.
outputArray.push(msg.work_mode ? { payload: workModeAction, topic:"user_work_mode"}: null);

// Set | rs485
const rs485Action = {
    "action": "select.select_option",
    "target": {
        "entity_id": [`select.${target}_rs485_control_mode`]
    },
    "data": {
         "option": String(msg.rs485)
    }
};
// Add to output array. Set null for no action.
outputArray.push(msg.rs485 ? { payload: rs485Action, topic: "rs485_control_mode" } : null);

// Store calls in msg to allow checking of loop results
msg.loop_result = { target, workModeAction, rs485Action };

// Return msg as third output
outputArray.push(msg);

// Outputs
return outputArray;

// loop through the number of batteries set by the user
msg.battery_index = 1; // base-1

// configurables
let workMode = null; // manual, anti-feed, ai, null = don't set or alter this setting
let rs485 = null;  // enable, disable, null = don't set or alter this setting

// mode select
switch (msg.marstek_master_battery_mode) {
    case "Manual control":
        workMode = "manual";
        rs485 = "disable";
        break;
    case "Marstek control":
        // the user should select a work mode via the Marstek App or via select.marstek_mX_user_work_mode
        rs485 = "disable";
        break;
    case "Full control":
        rs485 = "enable";
        break;
    default:
        node.status({fill:"red",shape:"ring",text:`Unhandled mode: ${msg.marstek_master_battery_mode}`});
        return null; // stop flow
}

// Continue
msg.work_mode = workMode;
msg.rs485 = rs485;
return msg;

// Step index down
msg.battery_index += 1;

// Continue
return msg;

// See: house_battery_control.yaml > input_select: > house_battery_control_pid_presets
const preset = RED.util.getMessageProperty(msg,"payload");
node.status({fill:"green",shape:"dot",text:`${preset}`});


// Default values
let Kp = 0; // Proportional gain
let Ki = 0; // Integral gain
let Kd = 0; // Differentiating gain
let Si = 0; // % Input smoothing (a Low pass moving average filter)
let So = 0; // % Output smoothing (a Low pass moving average filter)

switch (preset) {
    // For people who have unstable systems with every other preset
    case "Very safe":
        Kp = 0.1;
        Ki = 0.1;
        Si = 0; // without Kd, this is preferred to be zero.
        So = 10; // %
        break;
    // Good starting point for most
    case "Safe":
        Kp = 0.3;
        Ki = 0.3;
        Kd = 0.1;
        Si = 20; // %
        So = 0; // %
        break;
    // Gitcodebob's October 2025 settings for 3.2kWp PV and 5kW battery transformer power
    case "Regular":
        Kp = 0.3;
        Ki = 0.4;
        Kd = 0.8;
        Si = 50; // % This high amount of filtering allows the stronger Kd
        So = 10; // %
        break;
    default:
        // Don't change values
        return null;
}

// Prepare for three outputs
return [
    { "payload": Kp },
    { "payload": Ki },
    { "payload": Kd },
    { "payload": Si },
    { "payload": So }
];

// explain
const log = global.get("logger");
log(this,"Using self-consumption to charge with surplus PV")

// use the self consumption strategy to charge when there is PV surplus
msg.target = "Self-consumption";

// tell that strategy to disalow discharging
const DISABLE_DISCHARGE = true;
RED.util.setMessageProperty(msg,"advanced_settings.discharge_disabled",DISABLE_DISCHARGE,true);

return msg;

const handle = global.get('unhandledException');

handle(this);

return msg;

// Logger, usage: log(this, "");
const log = global.get('logger');
log(this, "Charge at **max. power**");

// INPUT
let batteries = msg.batteries;
let solution_array = [];

// build solution
msg.batteries.forEach(battery => {
    const calculatedPower = Number(battery.charging_max);

    solution_array.push({
        id: battery.id,
        mode: "charge",
        power: calculatedPower
    });

});

// return solution
msg.solutions = solution_array;
return msg;

// Logger, usage: log(this, "");
const log = global.get('logger');

// Which sub-strategy to use
msg.target = "Self-consumption";

// Set target grid consumption for the PID controller
msg.house_target_grid_consumption_in_w = msg.house_battery_strategy_charge_target_power;
// tell PID to avoid discharge solutions during charging.
RED.util.setMessageProperty(msg,"advanced_settings.discharge_disabled",true,true);

// Finally
log(this, `**Regulated** charging. Limit grid power: ${Math.floor(msg.house_battery_strategy_charge_target_power)} W`);
return msg;

const log = global.get("logger");

// Unkown export goal
log(this, `**${msg.charge.goal}**; Charge goal not recognized. Fallback to **Full Stop**`,"warn");

// Full stop
msg.target = "Full stop";

return msg;

// 1. Get the custom logger from global context
const log = global.get("logger");
log(this,`Charge until: ${msg.charge.goal}`);

// 2. Extract energy level from msg (default to 0 if property is missing)
const energy_remaining = Number(RED.util.getMessageProperty(msg, "batteries_available_energy")) || 0;
var energy_threshold = Number(RED.util.getMessageProperty(msg,"charge.threshold_energy"));
if (energy_threshold === undefined || energy_threshold <= 0){
    log(this,`Desired energy reserve invalid '${energy_threshold}' kWh. I've set it to ${Number(msg.batteries_max_energy).toFixed(1)} kWh`,"warn");
    energy_threshold = Math.floor(Number(msg.batteries_max_energy)*10)/10;
}

// 3. Logic: Determine if energy level is at or below zero
if (energy_remaining >= energy_threshold) {
    msg.charge.threshold_reached = true;

    // User friendly feedback via the custom logger
    log(this, `[STOP] Energy reserve at ${energy_remaining.toFixed(1)} / ${energy_threshold.toFixed(1)} kWh`, 'info');
} else {
    msg.charge.threshold_reached = false;

    // User friendly feedback via the custom logger
    log(this, `[OK] Energy reserve at ${energy_remaining.toFixed(1)} / ${energy_threshold.toFixed(1)} kWh.`, 'info');
}

// 4. Update node status for visual feedback in the editor
node.status({ 
    fill: msg.charge.threshold_reached ? "red" : "green", 
    shape: "dot", 
    text: `Threshold reached: ${msg.charge.threshold_reached}` 
});

return msg;

// 1. Get the custom logger from global context
const log = global.get("logger");
log(this, `Charge until: ${msg.charge.goal}`);

// 2. Extract average SoC and desired SoC level
let batteries = msg.batteries;
let soc_avg = 0;
let count = 0; // could use msg.battery_count
batteries.forEach(battery => {
    soc_avg += battery.soc;
    count++;
});
// 2.1 Current avg. SoC
soc_avg = Math.round(soc_avg / count * 100) / 100;
// 2.2 Charge until SoC (100% as fallback)
var soc_threshold = Number(RED.util.getMessageProperty(msg,"charge.threshold_soc"));
if (soc_threshold === undefined || soc_threshold < 12){
    log(this, `Desired SoC not set correctly '${soc_threshold}' %. I've set it to 100%.`,"warn");
    soc_threshold = 100;
}

// 3. Logic: Determine if SoC level is at or above desired level
if (soc_avg >= soc_threshold) {
    msg.charge.threshold_reached = true;

    // User friendly feedback via the custom logger
    log(this, `[STOP] Average SoC at ${soc_avg.toFixed(1)}/${soc_threshold.toFixed(1)} %.`, 'info');
} else {
    msg.charge.threshold_reached = false;

    // User friendly feedback via the custom logger
    log(this, `[OK] Average SoC at ${soc_avg.toFixed(1)}/${soc_threshold.toFixed(1)} %.`, 'info');
}

// 4. Update node status for visual feedback in the editor
node.status({
    fill: msg.charge.threshold_reached ? "red" : "green",
    shape: "dot",
    text: `Threshold reached: ${msg.charge.threshold_reached}`
});

return msg;

// 1. Get the custom logger from global context
const log = global.get("logger");
log(this,`Charge until: ${msg.charge.goal}`);

// 2. Extract energy level from msg (and handle missing properties)
const energy_remaining = Number(RED.util.getMessageProperty(msg, "batteries_available_energy")) || 0;
const energy_max = Number(RED.util.getMessageProperty(msg, "batteries_max_energy")) || 0;
if(energy_max == 0) log(this,`Batteries max energy missing! ${msg.batteries_max_energy}`);
// 2.1 Check if every battery's SoC is at or over the max
const isAtMax = msg.batteries.every(battery => battery.soc >= battery.soc_max);

// 3. Logic: Determine if energy level is over or near 99.9% of max eneregy
if (isAtMax) {
    msg.charge.threshold_reached = true;

    // User friendly feedback via the custom logger
    log(this, `[STOP] All batteries are full or at SoC_max_ (${energy_remaining} kWh).`, 'info');
} else {
    msg.charge.threshold_reached = false;

    // User friendly feedback via the custom logger
    log(this, `[OK] Battery level ${energy_remaining.toFixed(2)} kWh below ${energy_max.toFixed(2)} kWh.`, 'info');
}

// 4. Update node status for visual feedback in the editor
node.status({ 
    fill: msg.charge.threshold_reached ? "red" : "green", 
    shape: "dot", 
    text: `Threshold reached: ${msg.charge.threshold_reached}` 
});

return msg;

// If any solar surplus remains, soak it up instead of waiting in Full stop.
msg.target = "Charge PV";
return msg;

const log = global.get("logger");

if(msg.charge.threshold_reached){
    log(this,`Stop charging, Charge until ${msg.charge.goal} reached.`);
} else {
    log(this,`Continue charging.`);
}

return msg;

// Lower bound
let output = msg.charge.threshold_energy || 0; // kWh
output = Math.max(output, 0);

// Upper bound
output = Math.min(output, msg.batteries_max_energy);

node.status({fill:"blue",shape:"dot",text:`${output}`});

// Output
msg.payload = output;

return msg;

const handle = global.get('unhandledException');

handle(this);

return msg;

// logger, usage: log(this, "");
const log = global.get('logger');

// 1.   Input
// Conversion
const CONVERSION_RATE = parseFloat(RED.util.getMessageProperty(msg, "dynamic.value_conversion")) || 1;

// extract start/end date objects
const start_cheapest = new Date(RED.util.getMessageProperty(msg,"dynamic.data_cheapest.start"));
const start_expensive = new Date(RED.util.getMessageProperty(msg,"dynamic.data_expensive.start"));
const end_cheapest = new Date(RED.util.getMessageProperty(msg, "dynamic.data_cheapest.end"));
const end_expensive = new Date(RED.util.getMessageProperty(msg, "dynamic.data_expensive.end"));
let isCheapestNext = true;
// stored price cheapest period
let lastCheapestPrice = context.last_cheapest_price || 0; // unit: cents/kWh
const nextCheapestPrice = parseInt(RED.util.getMessageProperty(msg, "dynamic.data_cheapest.average") / CONVERSION_RATE * 100); // unit: cents/kWh 
// average price expensive period
const lastExpensivePrice = parseInt(RED.util.getMessageProperty(msg, "dynamic.data_expensive.average") / CONVERSION_RATE * 100); // unit: cents/kWh
// threshold rate for cheapest period
const cheapestThresholdCents = parseInt(RED.util.getMessageProperty(msg, "dynamic.threshold_cheapest_cents")) || 0; // unit: cents
// threshold delta for expensive period
const deltaThresholdCents = parseInt(RED.util.getMessageProperty(msg, "dynamic.min_delta_cents")) || 0; // unit: cents

// 1.1  Enums
const STRATEGY = {
    CHEAPEST: RED.util.getMessageProperty(msg,"dynamic.strategy_cheapest") || "Charge", 
    NEUTRAL: RED.util.getMessageProperty(msg,"dynamic.strategy_default") || "Charge PV",
    EXPENSIVE: RED.util.getMessageProperty(msg,"dynamic.strategy_expensive") || "Self-consumption"
}

// 1.2  Defaults   
msg.dynamic.avg_cheapest_tariff = 0;
msg.dynamic.avg_expensive_tariff = 0;
msg.dynamic.avg_delta = 0;

// 2.   Tariffs
// Check if the Date objects are valid. If 'Invalid Date', stop and warn the user.
if (isNaN(start_cheapest.getTime()) || isNaN(start_expensive.getTime())) {
    // warn user
    log(this, `One or both dates are invalid. Cheap = ${start_cheapest}, Expensive = ${start_expensive}`,"warn");
    node.status({fill:"red",shape:"ring",text:`One or both dates are invalid: ${start_cheapest}, ${start_expensive}`});
    // fallback strategy
    flow.set("dynamic_strategy", STRATEGY.NEUTRAL);
    // proceed to UI
    return msg;
}

// Check if cheapest lies before expensive
if (start_cheapest < start_expensive) {
    log(this, `Cheapest moment lies before expensive moment. Cheapest moment: ${start_cheapest}, Expensive = ${start_expensive}`);
    // store the cheapest price to compare it with future expensive rates, to determine if it's economical to discharge.
    context.last_cheapest_price = nextCheapestPrice; // store for next iteration
    lastCheapestPrice = nextCheapestPrice; // used for this iteration
    log(this, `Cheapest price set to ${lastCheapestPrice} cents`);
}

if (lastCheapestPrice == 0) {
    log(this, `I can't recall the tariff used for charging. Cheapest price set to '${lastCheapestPrice}' cents`);
}

// delta > N cents ? -> charging is economical
const delta = (lastExpensivePrice - lastCheapestPrice); // unit: cents

// UI values
msg.dynamic.avg_cheapest_tariff = lastCheapestPrice;
msg.dynamic.avg_expensive_tariff = lastExpensivePrice;
msg.dynamic.avg_delta = delta;

// 3.   Strategy selection logic
// for string time
function isTimeInPeriod(startStr, endStr) {
    const now = new Date();
    const start = new Date(startStr);
    const end = new Date(endStr);
    return (now >= start && now < end);
}
// 3.1  is_now - which period have we entered?
const now = new Date();
/** @type {boolean} */
const cheapestIsNow = now >= start_cheapest && now <= end_cheapest;
// const cheapestIsNow = RED.util.getMessageProperty(msg,"dynamic.data_cheapest.is_now");
/** @type {boolean} */
const expensiveIsNow = now >= start_expensive && now <= end_expensive;
// const expensiveIsNow = RED.util.getMessageProperty(msg, "dynamic.data_expensive.is_now");

// 3.2  Set strategy
// Default strategy
msg.dynamic.strategy = STRATEGY.NEUTRAL;

// Cheapest period?
if (cheapestIsNow && lastCheapestPrice <= cheapestThresholdCents) {
    msg.dynamic.strategy = STRATEGY.CHEAPEST;
    // Explain
    log(this, `**Cheapest period is NOW**, price limit: ${lastCheapestPrice}/${cheapestThresholdCents} cents OK`);
} else if(cheapestIsNow) {
    // Explain why skipped
    log(this, `**Cheapest period SKIPPED**, the cheapest period is too expensive today. Price limit: ${lastCheapestPrice}/${cheapestThresholdCents} cents.`);
}

// Expensive period?
if (expensiveIsNow && delta >= deltaThresholdCents) {
    msg.dynamic.strategy = STRATEGY.EXPENSIVE;
    // Explain
    log(this, `**Expensive period is NOW**, price delta: ${delta}>=${deltaThresholdCents} cents OK`);
} else if(expensiveIsNow) {
    // Explain why skipped
    log(this, `**Expensive period SKIPPED**, price delta too small: ${lastExpensivePrice}-${lastCheapestPrice}=${delta} cents <= ${deltaThresholdCents} cents`); 
}

// 3.3  Store the strategy in flow-context. The 'Execution' area will read from the flow-context.
flow.set("dynamic_strategy",msg.dynamic.strategy);

// 4.   User feedback
// Node status
node.status({ fill: "blue", shape: "dot", text: `${msg.dynamic.strategy}; ${cheapestIsNow} + ${expensiveIsNow} @ ${delta} cents` });
// Explain
log(this, `Strategy set to **${msg.dynamic.strategy}**`);

// Proceed to UI update
return msg;

