/**
 * Formats an array of strings into a single string:
 * - Each item starts with 4 spaces.
 * - Each item ends with a comma, except the last one.
 * - Items are separated by a newline.
 */
function formatTemplateArray(array) {
    if (!array || array.length === 0) {
        return "";
    }

    const indent = "    "; 

    const formatted_lines = array.map((element, index) => {
        const suffix = (index < array.length - 1) ? "," : "";
        return `${indent}${element}${suffix}`;
    });

    return formatted_lines.join('\n');
}

