msg.advanced_settings ||= {}; // creates 'advanced_settings' if it does net yet exist
let mas = msg.advanced_settings;

// disable charge or dicharge solutions and changes them to stop solutions
RED.util.setMessageProperty(msg,"advanced_settings.discharge_disabled", mas.discharge_disabled ?? false);
RED.util.setMessageProperty(msg,"advanced_settings.charge_disabled", mas.charge_disabled ?? false);
// The nullish coalescing (??) operator is a logical operator that returns its right-hand side operand when its left-hand side operand is null or undefined

// 3. continue
return msg;

// Notes for home battery tinkerer friends:
// Using RED.util.getMessageProperty / RED.util.setMessageProperty is more performant and safe.
// If any part of the path ("foo.bar") is missing, it will return 'undefined' without throwing an error.

// INPUTS
let P1_error_last = Number(context.get("p1_error_last"))||0; // last error signal level in W
let dampening = Number(flow.get("house_battery_control_error_signal_dampening")) || 0; // 0% - 100%
dampening = dampening / 100; // to Number

// Process Variable (PV) currently measured grid power, Setpoint (SP) desired grid power, set 0 for NoM
var P1_power = msg.grid_power; // PV: consume is positive, supply to grid is negative
var P1_setpoint = Number(RED.util.getMessageProperty(msg,"house_target_grid_consumption_in_w")); // SP: target value

// Calc error signal
let P1_error_unfiltered = P1_setpoint - P1_power;

// Simple averaging filter
let P1_error_filtered = (1 - dampening) * P1_error_unfiltered + (dampening) * P1_error_last;

// OUTFLOW
context.set("p1_error_last", P1_error_filtered);

// OUTPUT
msg.grid_error = P1_error_filtered; // W (watt), error signal = SP - PV
return msg;

var P1_power = msg.grid_power||0;
var P1_last_power = Number(context.get("p1_last_power")) ||0;

// PV derivative
var p1_derivative = P1_power - P1_last_power; // devided by unity seconds

// OUTFLOW
context.set("p1_last_power", P1_power);

// OUTPUT
msg.grid_derivative = p1_derivative;
return msg;

var P1_error = msg.grid_error||0;
var P1_last_error = Number(context.get("p1_last_error")) ||0;

// Error derivative
// note: error = -PV
// note: for simplicity we assume 1 second time steps
let p1_derivative = -(P1_error - P1_last_error); // devided by unity seconds

// OUTFLOW
context.set("p1_last_error", P1_error);

// OUTPUT
msg.grid_derivative = p1_derivative;
return msg;

// Deadband
// Stop processing if error is within .. Watt of target grid consumption.
// This to reduce CPU loads 

// Logger
const log = global.get("logger");

// P1
let P1_error = msg.grid_error; // Watt
let P1_deadband = 15; // Watt

// TODO
// based on last 'assignable power' and current error, if (error > assignable power) the system will never enter the deadband. And keep calculating each interation.
// however since we don't know if the is the assignable power will change, until we calculate the Control Value / load balancer
// we could end up in a deadlock if we simply stop execution here, based on assignable power alone.

// Within Deadband
if(Math.abs(P1_error) < P1_deadband) {
    // stop processing
    node.status({fill:"yellow",shape:"dot",text:`Halt, ${Math.round(Math.abs(P1_error))} W`});
    log(this, `**Stopped processing** ??power saving, ${Math.round(Math.abs(P1_error))} W is within deadband of ${P1_deadband} W`);
    msg.payload = 'halted by deadband';
    return msg;
} 

// Outside deadband
node.status({fill:"green",shape:"dot",text:`Pass, ${Math.round(Math.abs(P1_error))} W`});
return msg;

// Logger
var logdata = ""; // deprecated
const log = global.get("logger");

// Timing
let time_last = context.get('time_last') || Date.now(); // Milliseconds
let time_current = Date.now(); // Milliseconds
let time_delta = (time_current - time_last) / 1000; // Convert to seconds
// note: due to inherent 1 sec intervals of P1 meter we omit dt terms. This is mostly for bug checking and optimizations.

// Batteries
var B_power = msg.batteries_total_power; // charging is positive, discharging negative
var anti_windup_threshold = Number(flow.get("batteries_total_assignable_power")) || 0; // max available charge/discharge power.

// Process Variable (PV) currently measured grid power, Setpoint (SP) desired grid power, set 0 for NoM
var P1_power = msg.grid_power; // PV: consume is positive, supply to grid is negative
var P1_setpoint = flow.get("house_target_grid_consumption_in_w"); // SP: target value
var P1_error = msg.grid_error;  // W (watt), error signal = SP - PV
var P1_derivative = msg.grid_derivative; // Derivative of PV, not the error

// PID values
var Kp = flow.get("house_battery_control_kp") || 0.75;  // Proportional gain
var Ki = flow.get("house_battery_control_ki") || 0;     // Integral gain        Ki = Kp/Ti
var Kd = flow.get("house_battery_control_kd") || 0;     // Derivative gain      Kd = Kp*Td

// helpers
var I_integral_sum = context.get('I_integral_sum') || 0;
var Gm = flow.get("master_gain")||1; // gain scheduling

// optimizations
var hysteresis = flow.get("house_battery_control_hysteresis_in_w"); // W (watt)

// advanced settings
const isDischargeDisabled = RED.util.getMessageProperty(msg,"advanced_settings.discharge_disabled");
const isChargeDisabled = RED.util.getMessageProperty(msg,"advanced_settings.charge_disabled");

// -- PID regulator --
// Proportional term
let p_term = Gm * Kp * P1_error;

// Integral term
let integral_max = 0;
if (Ki > 0) {
    // Integral term | integrate
    I_integral_sum += P1_error; 
    // Integral term | apply anti-windup
    integral_max = anti_windup_threshold / Ki; // the anti-windup value is set to the current controlable range of the batteries, given by the previous load balancer calculations
    I_integral_sum = Math.min(Math.max(I_integral_sum, -integral_max), integral_max);
} else {
    // If Ki is 0, keep everything 0
    I_integral_sum = 0;
    integral_max = 0;
}

// Integral term | the term
let i_term = Ki * I_integral_sum;

// Integral term | explain
logdata += `anti_windup_threshold=${anti_windup_threshold}, integral_max=${integral_max}| `; // deprecated

// Differential term
let d_term = Gm * Kd * (-P1_derivative); // omitted '/time_delta'. inherent P1 refresh fequency.

// Total PID output
var PID_output = p_term + i_term + d_term; // W (watt), the control input for the battery packs

// Charging or Discharging states
var B_was_charging = context.get("batteries_charging_last") || false;
var B_is_charging = PID_output > 0 ? true : false;

// Explain
logdata += `U(${time_delta}s)[${PID_output}] = P(${Kp})[${p_term}] + I(${Ki})[${i_term}] + D(${Kd})[${d_term}] | `; // deprecated
log(this,`**PID Controller**`);
log(this,`Step size: ${time_delta} s`);
if(Kp == 0 && Ki == 0 && Kd == 0) {
    log(this, `<font color="orange">**Controller disabled**</font> All PID gains are 0. Choose a PID preset or set gains > 0.`,"warn");
} else {
    log(this, `P-term: ${Math.floor(p_term)} W @ P-gain ${Kp}`);
    log(this, `I-term: ${Math.floor(i_term)} W @ I-gain ${Ki} (max. ${anti_windup_threshold} W)`);
    log(this, `D-term: ${Math.floor(d_term)} W @ D-gain ${Kd}`);
    log(this, `**PID ${B_is_charging ? 'Charge' : 'Discharge'}:** ${Math.floor(p_term)} + ${Math.floor(i_term)} + ${Math.floor(d_term)} = **${Math.round(PID_output)} Watt**`);
}

// Only one of the advanced settings below can be active at once.
// Advanced setting | discharge disabled
if(isDischargeDisabled && !B_is_charging) {
    // log explain
    logdata += `Discharging disabled, PID unwound U(${time_delta})[0]=0+0+0 | `;
    log(this,'**PID set to 0**. Discharge was disabled via `msg.advanced_settings`.');
    // Set all PID terms to 0 and unwind integral sum
    PID_output = 0;
    p_term = 0;
    i_term = 0;
    d_term = 0;
    I_integral_sum = 0; // unwind
    // maintain charge scenario
    B_is_charging = true;

// Advanced setting | charge disabled
} else if (isChargeDisabled && B_is_charging) {
    // log explain
    logdata += `Charging disabled, PID unwound U(${time_delta})[0]=0+0+0 | `;
    log(this,'**PID set to 0**. Charge was disabled via `msg.advanced_settings`.');
    // Set all PID terms to 0 and unwind integral sum
    PID_output = 0;
    p_term = 0;
    i_term = 0;
    d_term = 0;
    I_integral_sum = 0; // unwind
    // maintain discharge scenario
    B_is_charging = false;

// Advanced setting | hysteresis
} else if 
// prevents excessive switching between (dis)charge mode around the zero point.
// if new PID_output lies within hysteresis, it will not switch charge mode. 
// set hysteresis to 0, to apply no hysteresis
(B_is_charging !== B_was_charging && Math.abs(PID_output) < hysteresis){
    // log explain
    logdata += `Hysteresis prevented charge mode switch from ${B_was_charging ? "charging" : "discharging"} to ${B_is_charging ? "charging" : "discharging"} as ${Math.abs(PID_output)}W <= ${hysteresis}(hyst) | `;
    log(this,`Hysteresis prevented charge mode switch from ${B_was_charging ? "charging" : "discharging"} to ${B_is_charging ? "charging" : "discharging"} as ${Math.abs(PID_output)}W <= ${hysteresis}(hyst)`);
    // prevent negative charging or positive discharging values (not allowed)
    PID_output = (PID_output < 0 && B_is_charging) || ((PID_output > 0 && !B_is_charging)) ? 0 : PID_output;
    // maintain previous scenario
    B_is_charging = B_was_charging;
} 

// save for next iteration
context.set("batteries_charging_last", B_is_charging); //boolean

// OUTFLOW
context.set("time_last", Date.now());
context.set("I_integral_sum", I_integral_sum);

// OUTPUT
RED.util.setMessageProperty(msg, "batteries_charging", B_is_charging, true);
RED.util.setMessageProperty(msg, "I_integral_sum", I_integral_sum,true);
msg.payload = Number(PID_output);


return [msg, // payload = PID_output
        { payload: Number(P1_error)},
        { payload: parseFloat(p_term)},
        { payload: parseFloat(i_term)},
        { payload: parseFloat(d_term)},
        { payload: B_is_charging},
        { payload: logdata }];

// INPUT
let PID_unfiltered = msg.payload; // W Watt
let PID_last = context.get("PID_last")||0; // W watt
let PID_damp = Number(flow.get("house_battery_control_pid_output_dampening"))||0; // 0% - 100%
PID_damp = PID_damp/100; // to Number

// simple averaging filter
let PID_filtered = (1-PID_damp)*PID_unfiltered + (PID_damp)*PID_last;

// OUTFLOW
context.set("PID_last", PID_filtered);

// OUTPUT
msg.payload = Number(PID_filtered)
return msg;

// INFLOW
let maxCharge = msg.batteries_max_charge_power||undefined;
let maxDischarge = msg.batteries_max_discharge_power||undefined;
let isCharging = msg.batteries_charging||undefined;
let PID_output = msg.payload; // Watt

// No failsafe
if (maxCharge === undefined || maxDischarge == undefined) {
    node.status({ fill: "red", shape: "dot", text: "disabled" });
    node.warn(`Output Failsafe is INACTIVE. Max charge or discharge limits are undefined`);
    // continue without safeguard
    return msg;
} 

// limit
let limit = 0;

// charging state unknown
if(isCharging === undefined) {
    // limit on lowest of charge / discharge
    limit = Math.min(maxCharge,maxDischarge);    
}
// charging state known
limit = isCharging ? maxCharge : maxDischarge;

// Safe output
if (PID_output <= limit){
    node.status({ fill: "green", shape: "dot", text: "safe" });
    return msg;
} 

// Unsafe, limit output
node.status({ fill: "yellow", shape: "ring", text: "power limiting" });
msg.payload = Number(limit);
return msg;

// Logger
const log = global.get("logger");
log(this,"**PID load distribution**");

// INPUT
var batteries = msg.batteries; // Battery configuration as provided by `Home Batteries IO` flow
var isCharging = msg.batteries_charging; // Are we in a battery charging scenario?
var hasChargingLimiter = flow.get("has_soc_charging_limiter") || false; // Battery life improvement
var hasReverseDischargePriority = flow.get("has_reverse_priority_discharge") || false; // Prioritize (dis)charging the same battery
const isDischargeDisabled = RED.util.getMessageProperty(msg,"advanced_settings.discharge_disabled"); // Disallow discharging?

// how much power do the batteries need to compensate?
var unassigned_power = Math.round(Math.abs(msg.payload)); // Power in W to compensate using batteries
log(this,`How much power do the batteries need to compensate: ${unassigned_power} W`);

// inits 
var solution_array = []; // load distribution solutions
var batteries_total_assignable_power = 0; // Current max. available total (dis)charge power. Exceeding this max should trigger the anti-windup routine for the Integral terms

// enums
const CMODE = {
    STOP: "stop", // Marstek batteries disconnect a relay when this is set or Power is 0W
    CHARGE: "charge",
    DISCHARGE: "discharge"
}

/**
** Battery life improvement (slow charge near max SoC)
* @param {number} soc
* @param {number} max_power
*/
function chargingLimiter(soc, max_power) {
    // Must be greater than zero
    let limitedPower = Math.max(0, max_power); 

    // Limit charge at high SoC
    if (hasChargingLimiter) {
        if (soc >= 98)      limitedPower = Math.min(300, limitedPower);
        else if (soc >= 95) limitedPower = Math.min(500, limitedPower);
        else if (soc >= 85) limitedPower = Math.min(1500, limitedPower);
    }
    return limitedPower;
}

/**
* battery life improvement (disconnects battery only if the battery is not used for ... seconds)
* @param {string | number} batteryID
* @returns {{id: string|number, mode: string, power: number}} battery solution
*/
function getStopSolution(batteryID) {
    // idle time, before stop is given to inverter relay.
    let time_idle_minimum = flow.get("idle_time_minutes")*60*1000||0; // Milliseconds

    // retrieve last registered active times for each battery based on their ids
    let lasttime_active = context.get("lasttime_active");

    // battery exists in register 
    if(lasttime_active[batteryID] !== undefined){
        // timing
        let time_last = lasttime_active[batteryID]; // Milliseconds 
        let time_now = Date.now();                  // Milliseconds 
        let time_idle = (time_now - time_last); // Milliseconds

        // battery is ready for STOP
        if (time_idle >= time_idle_minimum) {
            log(this,`Battery ${batteryID} [STOP]: 0 Watt and disconnect relay.`);
            return {id: batteryID, mode: CMODE.STOP, power:0}; // STOP or 0 Watt disconnects relay
        }

        // battery is kept IDLE, while awaiting minimum inactive time
        log(this,`Battery ${batteryID} [IDLE]: 1 Watt for ${Math.round((time_idle_minimum-time_idle)/1000)}s. Keep relay engaged.`);
        return { id: batteryID, mode: isCharging ? CMODE.CHARGE : CMODE.DISCHARGE, power: 1 }; // 1 Watt keeps battery IDLE, keeping relay engaged
    }

    // battery missing in register
    log(this,`Battery ${batteryID} [IDLE]: unknown last active time`);
    return getActiveSolution(batteryID, 1); // set a last active time and engage idle state
}

/**
* Get battery solution and register a last active time.
* @param {any} batteryID
* @param {number} assigned_power
* @returns {{id: string|number, mode: string, power: number}} battery solution
*/
function getActiveSolution(batteryID, assigned_power){
    // register battery as active
    let lasttime_active = context.get("lasttime_active")||[]; // last registered active times for each battery based on their ids
    lasttime_active[batteryID] = Date.now();
    context.set("lasttime_active", lasttime_active);

    // solution
    return {
        id: batteryID,
        mode: isCharging ? CMODE.CHARGE : CMODE.DISCHARGE,
        power: Math.round(assigned_power)
    };
}

// -- BATTERY DISCHARGE PRIORITY
if (!isCharging && hasReverseDischargePriority) {
   batteries.reverse();
}

// -- LOAD BALANCER --
batteries.forEach((/** @type {{ id: any; soc: number; soc_min: number; soc_max: number; rs485: string; charging_max: number; discharging_max: any; }} */ battery) => {

    // Explain
    log(this, `Battery ${battery.id}: ${battery.soc}% (min: ${battery.soc_min}%, max: ${battery.soc_max}%) ; RS485: ${battery.rs485=='enable'?'OK':'Nok'}`);

    // track if the battery should be skipped and why
    let skipReason = null;
    if (battery.rs485 !== "enable") {
        skipReason = `Battery ${battery.id} [SKIP]: skipped because RS485 is not 'enable'`;
    } else if (isCharging && battery.soc >= battery.soc_max) {
        skipReason = `Battery ${battery.id} [SKIP]: skipped because SoC (${battery.soc}%) >= Max (${battery.soc_max}%) while charging`;
    } else if (!isCharging && battery.soc <= (battery.soc_min - 1)) {
        skipReason = `Battery ${battery.id} [SKIP]: skipped because SoC (${battery.soc}%) <= Min (${battery.soc_min}%) while discharging`;
    } else if (!isCharging && isDischargeDisabled) {
        skipReason = `Battery ${battery.id} [SKIP]: skipped, discharging was disabled via msg property`;
    }

    // battery NOT AVAIABLE, skip via soft stop
    if (skipReason !== null) {
        log(this, skipReason);                      // communicate reason
        let solution = getStopSolution(battery.id); // get a soft stop solution for this battery
        solution_array.push(solution);              // add solution, do not update last active time.
        // next battery
        return; 
    }

    // battery is AVAILABLE, assign power
    let battery_assignable_power = isCharging ? chargingLimiter(battery.soc, battery.charging_max) : battery.discharging_max;
    let assign = Math.min(unassigned_power, battery_assignable_power);

    // select solution
    var solution;
    if (assign <= 0 ) {
        // no power solution
        solution = getStopSolution(battery.id); // a soft stop solution
        assign = 0;
    } else {
        // assigned power solution
        solution = getActiveSolution(battery.id, Math.round(assign));
    }
    solution_array.push(solution);

    // Explain: charge limiting
    if(unassigned_power > battery_assignable_power && battery_assignable_power < battery.charging_max) {
        log(this,`Charging limited to protect battery (${battery.soc}%): ${battery.charging_max}W -> ${battery_assignable_power}W`);
    }
    // Explain: solution result
    log(this, `<font color="#009ac7">Battery ${solution.id}</font>: assigned to ${solution.mode} with ${solution.power}W / ${isCharging ? battery.charging_max : battery.discharging_max }W max.`)

    // remaining power to assign
    unassigned_power -= assign;
    batteries_total_assignable_power += Number(battery_assignable_power);
});

// battery discharge priority - put solutions back in initial order
if (!isCharging && hasReverseDischargePriority) {
    solution_array.reverse();
}

// store solutions in msg
RED.util.setMessageProperty(msg, "solutions", solution_array,true);

// OUTFLOW
flow.set("batteries_total_assignable_power", batteries_total_assignable_power); // this is set for the anti-windup calculation in the earlier 'calculate corrections' node, which will pick this up during next iteration.

// OUTPUT
return msg; // to Home Battery flow

