    msg.sell.threshold_reached = true;

    // User friendly feedback via the custom logger
    log(this, `[STOP] All batteries are empty or at SoC_min_ (${energy_remaining} kWh).`, 'info');
} else {
    msg.sell.threshold_reached = false;

    // User friendly feedback via the custom logger
    log(this, `[OK] Battery level ${energy_remaining.toFixed(2)} kWh above 0 kWh.`, 'info');
}

// 4. Update node status for visual feedback in the editor
node.status({ 
    fill: msg.sell.threshold_reached ? "red" : "green", 
    shape: "dot", 
    text: `Threshold reached: ${msg.sell.threshold_reached}` 
});

return msg;

